mapper.select:
SELECT A.RM, A.Q_IDX, A.Q_CG, A.Q_TITLE, A.Q_DESC, A.Q_VIEW_CNT, A.Q_USER_ID, (substr(Q_REG_DE,0,10)) AS Q_REG_DE, A.Q_ANS_YN FROM ( SELECT ROWNUM rnum, S.* FROM ( SELECT RANK() OVER(ORDER BY Q_REG_DE ASC)RM, N.Q_IDX, N.Q_CG, N.Q_TITLE, N.Q_DESC, N.Q_VIEW_CNT, N.Q_USER_ID, (substr(Q_REG_DE,0,10)) AS Q_REG_DE, N.Q_ANS_YN FROM QUESTION N WHERE 1 = 1
AND (TITLE LIKE '%' || #{searchView} || '%' OR Q_USER_ID LIKE '%' || #{searchView} || '%')
AND Q_DEL_YN = 'N' AND Q_CG = 'JAVA' ORDER BY Q_IDX DESC ) S ) A WHERE rnum BETWEEN #{startRowNo} and #{endRowNo}
/*com.co.kr.question.dao.QuestionDao.selectJavaList - JAVA 질문과답변 목록 */

		SELECT COUNT(*) FROM QUESTION WHERE 1=1
		
         	AND (TITLE LIKE '%' || #{searchView} || '%' OR Q_USER_ID LIKE '%' || #{searchView} || '%')
        		
        
        /*com.co.kr.question.dao.QuestionDao.getListBaordJavaCount - 질문과답변 글 총개수 가져오기 */

SELECT Q_IDX, Q_CG, Q_TITLE, Q_DESC, Q_VIEW_CNT, Q_USER_ID, Q_REG_DE, Q_ANS_YN FROM QUESTION WHERE 1=1
AND Q_IDX = #{qIdx}
/*com.co.kr.question.dao.QuestionDao.questionDetail - 글 상세정보 */

mapper.update:
	
		UPDATE QUESTION
			SET
				Q_VIEW_CNT = Q_VIEW_CNT + 1
			WHERE Q_IDX = #{qIdx}		
	
	
