mapper.select:
 SELECT BINO
		FROM USERS
			WHERE USER_ID=#{user_id}
			
/* com.co.kr.bino.dao.BinoDao.binoSumCount - 접속자 회원의 bino 총액 가져오기 */

	SELECT SUM(BINO)
		FROM BINO
			WHERE USER_ID= #{userId}
			AND BINO_YN = 'Y'
			
/* com.co.kr.bino.dao.BinoDao.userBinoAdd - 적립 BINO 총액 */

	SELECT SUM(BINO)
		FROM BINO
			WHERE USER_ID= #{userId}
			AND BINO_YN = 'N'
			
/* com.co.kr.bino.dao.BinoDao.userBinoMinus - 사용 BINO 총액 */

mapper.update:
	
		UPDATE USERS
			SET
				BINO = #{bino}
			WHERE USER_ID = #{userId}		
	
	
/* com.co.kr.bino.dao.BinoDao.userBinoUpdate - 회원 bino 총액 수정 */
